mapper.select:
select
id, code, type, name, description, pid, url, menu_type, state, keystr, routing, create_by, create_time,
update_by, update_time
from sys_permission


        and id = #{id}


        and code = #{code}


        and type = #{type}


        and name = #{name}


        and description = #{description}


        and pid = #{pid}


        and url = #{url}


        and menu_type = #{menuType}


        and state = #{state}


        and keystr = #{keystr}


        and routing = #{routing}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_permission


        and id = #{id}


        and code = #{code}


        and type = #{type}


        and name = #{name}


        and description = #{description}


        and pid = #{pid}


        and url = #{url}


        and menu_type = #{menuType}


        and state = #{state}


        and keystr = #{keystr}


        and routing = #{routing}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
